package org.togu.updates;

import org.togu.common.AlphabetConstants;
import org.togu.common.Decryptor;
import org.togu.lab7.Rabin;

public class AuthorizationAgent {
	private static final Decryptor DECRYPTOR = new Rabin(AlphabetConstants.FULL_KEYBOARD, 17939, 4943, 61562);
	private static final String AUTHORIZATION_TOKEN = "";
	public static String getAuthorizationToken() {
		return DECRYPTOR.decrypt(AUTHORIZATION_TOKEN);
	}
}
